mapper.update:
update order_sys
set
  style = #{style,jdbcType=VARCHAR}
where id = #{id,jdbcType=VARCHAR}

mapper.select:
SELECT o.id, order_num AS orderNum, user_id AS userId, o.shop_id AS shopId, goods_id AS goodsId, customer_id AS customerId, o.address, customer_name AS customerName, customer_phone AS customerPhone, price, num, send_state AS sendState, payway, sender_id as senderId, remark, style, order_time AS orderTime, goods_name AS goodsName, specs, s.name as senderName FROM order_sys AS o INNER JOIN goods g ON o.goods_id = g.id and g.data_flag = 1 and g.shop_id = #{shopId,jdbcType=VARCHAR}
and DATE_FORMAT(o.order_time,'%Y-%m-%d') = #{date,jdbcType=VARCHAR}
and DATE_FORMAT(o.order_time,'%Y-%m') = #{date,jdbcType=VARCHAR}
INNER JOIN staff s on o.sender_id = s.id
